-- Modified Villains table with additional attributes
CREATE TABLE IF NOT EXISTS "Villains" (
    "Id" BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,  -- Primary key as BIGINT
    "Name" VARCHAR(255) NOT NULL,
    "Alias" VARCHAR(255),
    "PowerLevel" INT NOT NULL,                 -- Villain's power level
    "Origin" VARCHAR(255),                     -- Origin location or planet
    "Motive" TEXT,                             -- Villain's primary motive or objective
    "FirstAppearance" DATE,                    -- Date of the first appearance
    "Nemesis" VARCHAR(255)                     -- Hero or main opponent
);

-- Modified Superpowers table with VillainId as BIGINT
CREATE TABLE IF NOT EXISTS "Superpowers" (
    "Id" BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,   -- Primary key as BIGINT
    "Name" VARCHAR(255) NOT NULL,
    "PowerLevel" INT NOT NULL,
    "Description" TEXT,
    "VillainId" BIGINT NOT NULL,                               -- Foreign key as BIGINT

    CONSTRAINT FK_Superpowers_Villain FOREIGN KEY ("VillainId") REFERENCES "Villains"("Id")
);

